app.directive("fadey",function(){return{restrict:"A",link:function(scope,elm,attrs){var duration;duration=parseInt(attrs.fadey);if(isNaN(duration))duration=500;elm=jQuery(elm);elm.hide();elm.fadeIn(duration);return scope["delete"]=function(complete){return elm.fadeOut(duration,function(){if(complete)return complete.apply(scope)})}}}});