var app;app=angular.module("web",["ui.bootstrap"]);app.config(["$routeProvider","$locationProvider",function($routeProvider,$location){$routeProvider.when("/",{template:"index.scaml"});$routeProvider.when("/games/new",{template:"newgame.scaml",controller:"newGameCtrl"});$routeProvider.when("/games/my",{template:"mygames.scaml",controller:"myGamesCtrl"});return $routeProvider.when("/games/archive")}]);
app.controller("topMenuCtrl",["$scope","$rootScope","$location",function($scope,$rootScope,$location){$rootScope.location=window.location.pathname;return $scope.setRoute=function(route){return $location.path(route)}}]);